(function() {
    var a = "assets/css/" + DEMO_ADMINFLARE_VERSION + "/";
    $(document).ready(function() {
        var e = function(k) {
            var l = demoGetColor(DEMO_CURRENT_THEME),
                i = demoGetColor(k);
            $("#styleswitcher-buttons .btn-" + l).text(demoCapitaliseFirstLetter(l));
            var m = $("#styleswitcher-buttons .btn-code");
            m.next("br").remove();
            m.remove();
            $("#styleswitcher-buttons .btn-" + i).text("Primary").parent().next().prepend('<code class="btn-code">btn btn-primary</code><br>');
            var j = $("#styleswitcher-progress-bars .progress-code");
            j.prev("br").remove();
            j.remove();
            $("#styleswitcher-progress-bars .progress-" + i).parent().next().append('<br><code class="progress-code">&lt;div class="progress"&gt;...&lt;/div&gt;</code>')
        };
        var c = function(i) {
            i = demoTestThemeName(i);
            $("#bootstrap-css").attr("href", a + i + "/bootstrap.min.css");
            $("#adminflare-css").attr("href", a + i + "/adminflare.min.css");
            e(i);
            DEMO_CURRENT_THEME = i;
            demoSetCookieSettings()
        };
        var f = function(i) {
            DEMO_CURRENT_LAYOUT = demoTestLayoutName(i);
            $("body").removeClass("default-layout").removeClass("centered-layout").removeClass("fluid-layout");
            $("body").addClass(i + "-layout");
            $(window).resize();
            $(".wysihtml5-sandbox").css({
                width: "100%"
            });
            $("#MyGrid").datagrid("renderData");
            demoSetCookieSettings()
        };
        var h = function(i) {
            $("#theme_switcher .themes a").removeClass("active");
            $("#theme_switcher .themes a[data-theme=" + i + "]").addClass("active")
        };
        var d = function(i) {
            $("#theme_switcher .layouts a").removeClass("active");
            $("#theme_switcher .layouts a[data-layout=" + i + "]").addClass("active")
        };
        var b = function() {
            var i = $("#theme_switcher");
            i.css({
                left: $(window).width() - (i.hasClass("open") ? $("#theme_switcher").width() : 0)
            })
        };
       // e(DEMO_CURRENT_THEME);
       
        //h(DEMO_CURRENT_THEME);
        //d(DEMO_CURRENT_LAYOUT);
        $("#theme_switcher .button").click(function() {
            var i = $("#theme_switcher");
            i.animate({
                left: $(window).width() - (i.hasClass("open") ? 0 : $("#theme_switcher").width())
            });
            i.toggleClass("open");
            $(".button i", i).toggleClass("icon-cogs").toggleClass("icon-remove")
        });
        $(".themes a").click(function() {
            var i = $(this).attr("data-theme");
            h(i);
            c(i);
            return false
        });
        $(".layouts a").click(function() {
            var i = $(this).attr("data-layout");
            d(i);
            f(i);
            return false
        });
        b();
        $(window).resize(b)
    })
})();